script:
frappe.ui.form.on('Cluster', {
    onload: function(frm) {
        frm.set_query('region_manager', function() {
            return {
                filters: {
                    designation: 'מנהל מרחב'
                }
            };
        });

        frm.set_query('coordinator', function() {
            return {
                filters: {
                    designation: 'רכז הסדרה'
                }
            };
        });
    }
});

frappe.ui.form.on('Cluster', {
    sector: async function(frm) {
        if (!frm.doc.sector) return;

        // שליפה של פרטי הגזרה
        frappe.call({
            method: "frappe.client.get",
            args: {
                doctype: "Sector",
                name: frm.doc.sector
            },
            callback: function(res) {
                if (res.message) {
                    const sector = res.message;

                    // עדכון הרכז מה־Sector
                    frm.set_value("coordinator", sector.coordinator);

                    // שליפה של המרחב לפי Link
                    if (sector.region) {
                        frappe.call({
                            method: "frappe.client.get",
                            args: {
                                doctype: "Region",
                                name: sector.region
                            },
                            callback: function(region_res) {
                                if (region_res.message) {
                                    frm.set_value("region_manager", region_res.message.region_manager);
                                }
                            }
                        });
                    }
                }
            }
        });
    }
});

frappe.ui.form.on('Sector', {
    onload: function(frm) {

        frm.set_query('coordinator', function() {
            return {
                filters: {
