frappe.ui.form.on('Evacuee', {
    first_name(frm) {
        set_full_name(frm);
    },
    last_name(frm) {
        set_full_name(frm);
    }
});

function set_full_name(frm) {
    if (frm.doc.first_name || frm.doc.last_name) {
        frm.set_value('full_name', [frm.doc.first_name, frm.doc.last_name].filter(Boolean).join(' '));
    }
}

frappe.ui.form.on('Test Map', {
  refresh(frm) {
    // נטען את Leaflet אם עדיין לא נטען
    if (!window.leaflet_loaded) {
      $('<link rel="stylesheet" href="https://unpkg.com/leaflet/dist/leaflet.css">').appendTo('head');
      $('<script src="https://unpkg.com/leaflet/dist/leaflet.js">').appendTo('head');
      window.leaflet_loaded = true;
    }

    const lat = frm.doc.latitude || 32.0853;
    const lng = frm.doc.longitude || 34.7818;

    const html = `<div id="map" style="height: 300px;"></div>`;
    frm.fields_dict.custom_map.$wrapper.html(html);

    setTimeout(function () {
      const map = L.map('map').setView([lat, lng], 13);
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(map);
      L.marker([lat, lng]).addTo(map).bindPopup("המיקום שלך").openPopup();
    }, 300);
  }
});

frappe.ui.form.on('Lot', {
    lot_number(frm) {
        set_full_lot_number(frm);
    },
    plan_number(frm) {
        set_full_lot_number(frm);
    }
});

function set_full_lot_number(frm) {
    if (frm.doc.lot_number || frm.doc.plan_number) {
        frm.set_value('full_lot_number', [frm.doc.lot_number, frm.doc.plan_number].filter(Boolean).join('@'));
    }
}

frappe.ui.form.on('Customer', {
    validate: function(frm) {
        if (frm.doc.customer_type === 'זוג' && frm.doc.evacuee_2){
            frm.set_value('customer_name', frm.doc.custom_evacuee_a + " ו" + frm.doc.custom_evacuee_B);
        } else {
            frm.set_value('customer_name', frm.doc.custom_evacuee_a);
        }
    }
});

frappe.ui.form.on('Task', {
    refresh: function(frm) {
        // אם זו משימת-אב (is_group = true)
        if (frm.doc.is_group) {
            frappe.call({
                method: "frappe.client.get_list",
                args: {
                    doctype: "Task",
                    filters: {
                        parent_task: frm.doc.name
                    },
                    fields: ["name", "custom_estimated_cost", "custom_actual_cost"],
                    limit_page_length: 1000
                },
                callback: function(r) {
                    if (r.message) {
                        let total_estimated = 0.0;
                        let total_actual = 0.0;
                        r.message.forEach(task => {
                            total_estimated += parseFloat(task.custom_estimated_cost || 0);
                            total_actual += parseFloat(task.custom_actual_cost || 0);
                        });
                        frm.set_value("custom_estimated_cost", total_estimated);
                        frm.set_value("custom_actual_cost", total_actual);
                    }
                }
            });
        }
    }
});
